// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract Invoice {
    uint public invoiceNo = 0; 

    struct Data{
        string BuyerPan;
        string SellerPan;
        uint32 Amount;
        uint Time ;
        bool PaymentStatus;
    }

    mapping(uint => Data) public InoviceData;
    mapping(string => uint[]) private BuyerInvoices;

    function generateInvoice(string calldata _buyerPan, string calldata _sellerPan, uint32 _amt, bool _pmtStatus) external returns(uint){
        require(bytes(_buyerPan).length > 0,"Invalid buyer PAN");
        require(bytes(_sellerPan).length > 0,"Invalid seller PAN");
        require(_amt >= 0 ,"Invalid Amount");

        invoiceNo++;
        InoviceData[invoiceNo] = Data(_buyerPan,_sellerPan,_amt,block.timestamp,_pmtStatus);
        BuyerInvoices[_buyerPan].push(invoiceNo);
        return invoiceNo;
    }

    function checkPmtStatus(uint _invoiceNo) external view returns(bool) {
        require(_invoiceNo > 0 && _invoiceNo <= invoiceNo ,"Invalid Invoice No.");
        return InoviceData[_invoiceNo].PaymentStatus;
    }

    function updatePmtStatus(uint _invoiceNo) external returns(bool) {
        require(_invoiceNo > 0 && _invoiceNo <= invoiceNo ,"Invalid Invoice No.");
        Data storage invoiceData = InoviceData[_invoiceNo];
        invoiceData.PaymentStatus = !invoiceData.PaymentStatus;
        return invoiceData.PaymentStatus;
    }

    function getBuyerInvoices(string calldata _buyerPan) external view returns(uint[] memory){
        require(bytes(_buyerPan).length > 0,"Invalid buyer PAN");
        return BuyerInvoices[_buyerPan];
    }
}
